//To account for multiple phone formats in SFDC
ani2 = "({ANI.substr(1,3)}) {ANI.substr(4,6)}-{ANI.substr(7,10)}"
ani3 = "{ANI.substr(1,3)}-{ANI.substr(4,6)}-{ANI.substr(7,10)}"


	// Salesforce credentials for NA environment
	ASSIGN userName="user@usaenterprises.com"
	ASSIGN password="us@enterprises"
	ASSIGN securityToken="abc123def456ghi789ABC!@#"

	ASSIGN consumerKey="abcdefghijklmnopqrstuvwxyz1234567890ABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890abcdefghijklm"
	ASSIGN consumerSecret="123456789098765432"

	// Use our REST Proxy for the SF access token
	ASSIGN tokenproxy = GetRESTProxy()  

	// urlencode the login credentials and the security token since we are putting them 
	// on the URL as parameters 
	ASSIGN password=password.urlencode
	ASSIGN userName=userName.urlencode
	ASSIGN securityToken=securityToken.urldecode
	ASSIGN consumerKey=consumerKey.urlencode
	ASSIGN consumerSecret=consumerSecret.urlencode

	// create the token request parameters string, and the URL string
	tokenRequestParams=""
	tokenRequestParams.append("grant_type=password")
	tokenRequestParams.append("&client_id={consumerKey}")
	tokenRequestParams.append("&client_secret={consumerSecret}")
	tokenRequestParams.append("&username={userName}")
	tokenRequestParams.append("&password={password}{securityToken}")

	// create a tokenRequestURL based on whether this is a production or sandbox envirnment
	ASSIGN tokenRequestURL="https://login.salesforce.com/services/oauth2/token"

	// get the security token.
	ASSIGN tokenResult=tokenproxy.MakeRestRequest(tokenRequestURL, tokenRequestParams, 0, "POST")
	ASSIGN myToken=tokenResult.access_token.$value

	// create base URL of Salesforce instance
	ASSIGN baseURL = tokenResult.instance_url.$value

	//create query URL
	ASSIGN queryURL = "{baseURL}/services/data/v20.0/query"


	// create access token
	ASSIGN accessToken="Bearer "
	accessToken.append("{myToken}")
	tokenProxy.AddHeader("Authorization",accessToken)	
	tokenProxy.ContentType = "application/json"

	///////////////////////////////////////////////////////////////////////////////
	//
	// SOQL Salesforce Quesry to Find Contact information
	//
	///////////////////////////////////////////////////////////////////////////////

	// Create Salesforce SOQL query for the Contact and company name based on the incoming ANI
	myQuery="SELECT Id, Name, FirstName, LastName, AssistantName, Account.Name, Phone, MobilePhone, MailingStreet FROM Contact WHERE Phone = '{ANI}' OR MobilePhone = '{ANI}' limit 1"

	// Make a query call into Salesforce
	ASSIGN queryResult = tokenproxy.MakeRestRequest(queryURL, "q={myQuery}", 0, "GET")
//	
//	
	// Get the customer and account name based on the incoming ANI
	// Account manager (Agent ID) uses the Assistant field in Salesforce Contact record
	ASSIGN myRecord = queryResult.records
	ASSIGN ContID = "{myRecord[1].Id}"
	ASSIGN contactName = "{myRecord[1].Name}"
	ASSIGN contactFirstName = "{myRecord[1].FirstName}"
	ASSIGN contactLastName = "{myRecord[1].LastName}"
	ASSIGN AccountName = "{myRecord[1].Account.Name}"
	ASSIGN myAgent = "{myRecord[1].AssistantName}"
	ASSIGN mailingAddress = "{myRecord[1].MailingStreet}"


	///////////////////////////////////////////////////////////////////////////////
	//
	// SOQL Salesforce Quesry to Find Account information
	//
	///////////////////////////////////////////////////////////////////////////////

myQuery2 = "SELECT id, Name, Phone, Customer_Level__c, owner.name, owner.extension FROM Account WHERE Phone = '{ANI}' OR Phone = '{ANI2}' OR Phone = '{ANI3}'  order by createdDate limit 1")

	// Make a second query call into the parent account 
	ASSIGN queryResult2 = tokenproxy.MakeRestRequest(queryURL, "q={myQuery2}", 0, "GET")

// Get the customer and account name based on the incoming ANI
myRecord = queryResult2.records
accountName = "{myRecord[1].Name}"
SFAccountId = "{myRecord[1].Id}"
accountOwnerName = "{myRecord[1].Owner.Name}"
accountOwnerAgentID = "{myRecord[1].Owner.Extension}"
accountCustomerLevel = "{myRecord[1].Customer_Level__c}"

// If contact was found, pop contact, if no contact was found, pop new contact page and add phone number to record
IF contID.length > 0
{
	ASSIGN popURL = "https://na17.salesforce.com/{contID}"
}
ELSE
{
	ASSIGN popURL = "https://na17.salesforce.com/003/e?con10={ani}"
